import React, { useState } from "react";
import { useForm, } from "react-hook-form";


const Formwithhook = () => {
    const [userInfo, setUserinfo] = useState()
    const { register, handleSubmit, formState: { errors }  } = useForm()
    const Submit = (data) => {
        setUserinfo(data)
        console.log(data)  
    }
    console.log(errors)
    return (
        <div>
            <h2>Form with form-hook library</h2>
            <div>{JSON.stringify(userInfo, undefined, 2)}</div>
            <form >
                <div>
                    <label>username: </label>
                    <input type="text" name="username" placeholder="username" {...register("username",{required: "username is required"})}></input>
                </div>
                 {errors.username && <p>Please check the username</p>}
                <div>
                    <label>email: </label>
                    <input type="text" name="email" placeholder="email" {...register("email",{required: "email is required"})}></input>
                </div>

                <div>
                    <label>password: </label>
                    <input type="password" name="password" placeholder="password" {...register("password",{required: "password is required"})}></input>
                </div>

                <button type="button" onClick={handleSubmit(Submit)}>Register</button>
            </form>
        </div>
    )
}
export default Formwithhook;
