import React from "react";
import { useState } from "react";

const handleRegister = () =>{
    console.log(value)
}
const Formwithhook = () =>{
    const initialValue = {username: "", email: "", password: ""}
    const[value, setValue] = useState(initialValue)
    return(
        <div>
        <h2>Form with form-hook library</h2>
        <form>
            <div>
            <label>username: </label>
            <input type="text" name="username" placeholder="username" value={value.username} onChange={(e) => setValue({...value, username: e.target.value})}></input>
            </div>
            <div>
            <label>email: </label>
            <input type="text" name="email" placeholder="email" value={value.email} onChange={(e) => setValue({...value, email: e.target.value})}></input>
            </div>
            <div>
            <label>password: </label>
            <input type="text" name="password" placeholder="password" value={value.password} onChange={(e) => setValue({...value, password: e.target.value})}></input>
            </div>
            <button type="button" onClick={handleRegister()}>Register</button>
        </form>
        </div>
    )
}
export default Formwithhook;
